import React, { useState } from 'react';

function Avatar({ avatar, removeAvatar }) {
    let message;

    message = <div className="profile" style={{ width: '30%', margin: 'auto' }}>
        <div className="card">
            <div className="card-content">
                <div className="media">
                    <div className="media-left">
                        <figure className="image is-48x48">
                            {avatar.user.id ? <img src={avatar.user.avatar} alt="test" /> : ""}
                        </figure>
                    </div>
                    <div className="media-content">
                        <p className="title is-4">{avatar.user.first_name} {avatar.user.last_name}</p>
                        <p className="subtitle is-6">{avatar.user.email}</p>
                    </div>
                </div>

                <div className="content">
                    Lorem ipsum dolor sit amet, consectetur adipiscing elit.
        Phasellus nec iaculis mauris. <a>@bulmaio</a>.
        <a href="#">#css</a> <a href="#">#responsive</a>
        <button className="button" onClick={() => removeAvatar(avatar.user.id)}>x</button>
                    <br />
                    <time> {avatar.time}</time>
                </div>
            </div>
        </div>
    </div>
    return (
        <div>
            {avatar.isLoading ? "Loading..." : message}
        </div>
    );
}

function AvatarForm({ addAvatar }) {
    const [value, setValue] = useState("");

    const submitForm = e => {
        e.preventDefault();
        addAvatar(value);
        setValue("");
    }
    return (
        <form onSubmit={submitForm}>
            <div className="field">
                <div className="control">
                    <input className="input is-primary" type="text" placeholder="Primary input" value={value} onChange={e => setValue(e.target.value)} />
                </div>
            </div>
        </form>
    );
}


export { Avatar, AvatarForm };
