}

function TodoForm({ addTodo }) {
    const [value, setValue] = useState("");

    const submitForm = e => {
        e.preventDefault();
        addTodo(value);
        setValue("");
    }
    return (
        <form onSubmit={submitForm}>
            <input type="text" value={value} onChange={e => setValue(e.target.value)} />
        </form>
    );
}


export { Todos, TodoForm };
