import React, { useState } from 'react';

function Todos({ avatar, index, completeTodo }) {
    return (
        <div className="profile">
            <div className="details-item">{avatar.user.id ? <img src={avatar.user.avatar} alt="test" /> : ""}</div>
            <div className="details-item">
                <span>{avatar.user.first_name} {avatar.user.last_name}</span><br />
                {avatar.user.email}<br />
                {avatar.time}
            </div>
            <div className="details-item del-btn">
                <button onClick={() => completeTodo(avatar.user.id)}>x</button>
            </div>
        </div>
    );
}

function TodoForm({ addTodo }) {
    const [value, setValue] = useState("");

    const submitForm = e => {
        e.preventDefault();
        addTodo(value);
        setValue("");
    }
    return (
        <form onSubmit={submitForm}>
            <input type="text" value={value} onChange={e => setValue(e.target.value)} />
        </form>
    );
}


export { Todos, TodoForm };
