import React, { useState } from 'react'
import './App.css'
import { Todos, TodoForm } from './components/Todos';
import axios from 'axios';

function App() {
  const [avatars, setAvatar] = useState([]);
  const addTodo = text => {
    axios.get('https://reqres.in/api/users/' + text)
      .then((res) => {

        const newAvatar = [ ...avatars, { user: res.data.data, isError: false, isLoading: false } ];

        setAvatar(newAvatar);
      });
  }

  const updateTodo = index => {
    const newTodos = [...avatars];
    var isCompleted = newTodos[index].isCompleted;
    if (!isCompleted) {
      newTodos[index].isCompleted = true;
    } else {
      newTodos[index].isCompleted = false;
    }
    setAvatar(newTodos);
  }

  return (
    <div className="App">
      <header className="App-header">
        <h1>Reactors</h1>
        <TodoForm addTodo={addTodo} />
        {avatars.map((avatar, index) => (<Todos key={index} avatar={avatar} index={index} completeTodo={updateTodo} />))}
      </header>
    </div>
  );


}

export default App;
