import React, { useState } from 'react';

function Avatar({ avatar, removeAvatar }) {
    let message;

    message = <div className="profile">
        <div className="card">
            <div className="card-image">
                <figure className="image is-4by3">
                    <img src="https://bulma.io/images/placeholders/1280x960.png" alt="Placeholder image" />
                </figure>
            </div>
            <div className="card-content">
                <div className="media">
                    <div className="media-left">
                        <figure className="image is-48x48">
                            {avatar.user.id ? <img src={avatar.user.avatar} alt="test" /> : ""}
                        </figure>
                    </div>
                    <div className="media-content">
                        <p className="title is-4">{avatar.user.first_name} {avatar.user.last_name}</p>
                        <p className="subtitle is-6">{avatar.user.email}</p>
                    </div>
                </div>

                <div className="content">
                    Lorem ipsum dolor sit amet, consectetur adipiscing elit.
        Phasellus nec iaculis mauris. <a>@bulmaio</a>.
        <a href="#">#css</a> <a href="#">#responsive</a>
                    <br />
                    <time datetime="2016-1-1">11:09 PM - 1 Jan 2016</time>
                </div>
            </div>
        </div>
        <div className="details-item">{avatar.user.id ? <img src={avatar.user.avatar} alt="test" /> : ""}</div>
        <div className="details-item">
            <span>{avatar.user.first_name} {avatar.user.last_name}</span><br />
            {avatar.user.email}<br />
            {avatar.time}
        </div>
        <div className="details-item del-btn">
            <button className="button" onClick={() => removeAvatar(avatar.user.id)}>x</button>
        </div>
    </div>
    return (
        <div>
            {avatar.isLoading ? "Loading..." : message}
        </div>
    );
}

function AvatarForm({ addAvatar }) {
    const [value, setValue] = useState("");

    const submitForm = e => {
        e.preventDefault();
        addAvatar(value);
        setValue("");
    }
    return (
        <form onSubmit={submitForm}>
            <div className="field">
                <div className="control">
                    <input className="input is-primary" type="text" placeholder="Primary input" value={value} onChange={e => setValue(e.target.value)} />
                </div>
            </div>
        </form>
    );
}


export { Avatar, AvatarForm };
