import React, { useState } from 'react'
import './App.css'
import { Todos, TodoForm } from './components/Todos';

function App() {
  const [todos, setTodos] = useState([
    { text: "lorem  ipsum1", isCompleted: false },
    { text: "lorem  ipsum2", isCompleted: false },
    { text: "lorem  ipsum3", isCompleted: false },

  ]);
  const addTodo = text => {
    const newTodos = [...todos, { text }];
    setTodos(newTodos);
  }

  const updateTodo = index => {
    const newTodos = [...todos];
    let isCompleted = newTodos[index].isCompleted;
    if (isCompleted === false) {
      isCompleted = true;
    } else {
      isCompleted = false;
    }
    setTodos(newTodos);
  }

  return (
    <div className="App">
      <header className="App-header">
        <h1>Reactors</h1>
        <TodoForm addTodo={addTodo} />
        {todos.map((todo, index) => {
          return (<Todos key={index} todo={todo} index={index} completeTodo={updateTodo} />)
        })}
      </header>
    </div>
  );


}

export default App;
