import React, { useState } from 'react'
import './App.css'
import { Todos, TodoForm } from './components/Todos';
import axios from 'axios';

function App() {
  const [todos, setTodos] = useState([
    { text: "lorem  ipsum1", isCompleted: false },
    { text: "lorem  ipsum2", isCompleted: false },
    { text: "lorem  ipsum3", isCompleted: false },

  ]);
  const addTodo = text => {
    // const newTodos = [...todos, { text }];
    axios.get('https://reqres.in/api/users/' + text)
      .then((res) => {

        const newTodos = [  res.data.data ];

        setTodos(newTodos);
        // this.setState({ user: res.data.data, isError: false, isLoading: false })
      });
  }

  const updateTodo = index => {
    const newTodos = [...todos];
    var isCompleted = newTodos[index].isCompleted;
    if (!isCompleted) {
      newTodos[index].isCompleted = true;
    } else {
      newTodos[index].isCompleted = false;
    }
    setTodos(newTodos);
  }

  return (
    <div className="App">
      <header className="App-header">
        <h1>Reactors</h1>
        <TodoForm addTodo={addTodo} />
        {todos.map((todo, index) => (<Todos key={index} todo={todo} index={index} completeTodo={updateTodo} />))}
      </header>
    </div>
  );


}

export default App;
