import React, { useState } from 'react';

function Todos({ todo, index, completeTodo }) {
    return (
        <div>
            <h1 style={{ textDecoration: todo.isCompleted ? "line-through" : "" }} >{todo.text}</h1>
            <button onClick={() => completeTodo(index)}>qwe</button>
        </div>
    );
}

function TodoForm({ addTodo }) {
    const [value, setValue] = useState("");

    const submitForm = e => {
        e.preventDefault();
        addTodo(value);
        setValue("");
    }
    return (
        <form onSubmit={submitForm}>
            <input type="text" value={value} onChange={e => setValue(e.target.value)} />
        </form>
    );
}


export { Todos, TodoForm };
